// Helper function to get kecamatan by kode
export function getKecamatanByKode(kode: string): KecamatanCoord | undefined {
  return kecamatanData.find(k => k.kode === kode);
}

// Helper function to get kecamatan by kabupaten
export function getKecamatanByKabupaten(kabupaten: string): KecamatanCoord[] {
  return kecamatanData.filter(k => k.kabupaten === kabupaten);
}

// Helper to update pengurus status based on DPC data
export function syncKecamatanWithDPC(dpcData: { kode: string; hasPengurus: boolean }[]): void {
  dpcData.forEach(dpc => {
    const kec = kecamatanData.find(k => k.kode === dpc.kode);
    if (kec) {
      kec.adaPengurus = dpc.hasPengurus;
    }
  });
}
