// Helper functions
export const getBidangList = (): string[] => {
  const bidangSet = new Set(rkratData.map(item => item.bidang));
  return Array.from(bidangSet).sort();
};

export const getProgramUnggulanList = (): string[] => {
  const programSet = new Set(rkratData.map(item => item.programUnggulan).filter(p => p !== ""));
  return Array.from(programSet).sort();
};

export const formatCurrency = (amount: number): string => {
  return new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: 'IDR',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0,
  }).format(amount);
};
